import logo from "./logo.svg";
import "./App.css";
import { Parser } from "html-to-react";

const rawHTML = `
<div>
  <h1>The Second Example</h1>
  <p>The <strong>rat</strong> hates the <strong>cat</strong></p>
  <p><i>This is something special</i></p>
  <hr/>
  <div>
    <img src="https://www.kindacode.com/wp-content/uploads/2021/06/pi-2.jpeg" width="800"/>
  </div>
  <hr/>
  <h4>Just Another Heading</h4>
	<form>
  <label>
    Name:
    <input type="text" name="name" />
  </label>

</form>
</div>
`;

function App() {
	function handleSubmit(event) {
		alert("Your favorite flavor is: " + this.state.value);
		event.preventDefault();
	}
	return (
		<div style={container} onSubmit={handleSubmit}>
			{Parser().parse(rawHTML)}
			<input type="submit" value="Submit" />
		</div>
	);
}

const container = {
	width: 800,
	margin: "auto",
};

export default App;
