import React from "react";
declare type ButtonType = "submit" | "reset" | "button";
export interface ButtonProps {
    children: React.ReactNode;
    className?: string;
    disabled?: boolean;
    id?: string;
    onClick?: () => void;
    type?: ButtonType;
}
export default function Button({ children, className, disabled, id, onClick, type }: ButtonProps): JSX.Element;
export {};
